import Home from './components/Home'
import './App.css';

function App() {
  const scrollToHeader = () => {
    const header = document.getElementById('header');
    if (header) {
      header.scrollIntoView({ behavior: 'smooth' });
    }
  };
  return (
  <>
  <div className='relative' >
      <Home/>
  </div>
  <i
          className="sticky bottom-0 flex items-end justify-end p-4 text-5xl font-bold text-black bg-transparent cursor-pointer  bi bi-arrow-up-circle-fill" onClick={scrollToHeader}
          
        ></i>
  </>

  );
}

export default App;
