import React from 'react'
import 'bootstrap-icons/font/bootstrap-icons.css';
const Pencil = () => {
    const scrollToHeader = () => {
        const header = document.getElementById('header');
        if (header) {
          header.scrollIntoView({ behavior: 'smooth' });
        }
      };
  return (
   
        <button><i
        className="sticky bottom-0 right-0 flex items-end justify-end p-4 text-5xl font-bold text-black bg-transparent cursor-pointer hover:bg-indigo-800 bi bi-arrow-up-circle-fill" onClick={scrollToHeader}
        
      ></i></button>
      
  )
}

export default Pencil
