import React, { useState } from "react";

const Faq = () => {
  const faqData = [
    {
      question: "What courses do you offer?",
      answer: "We offer a variety of courses in web development, data science, machine learning, and more. Our courses are designed to cater to both beginners and advanced learners.",
    },
    {
      question: "How are the courses delivered?",
      answer: "Our courses are delivered through a combination of video lectures, interactive exercises, and real-world projects. You will have access to all course materials online and can learn at your own pace.",
    },
    {
      question: "Do you provide certifications?",
      answer: "Yes, upon successful completion of a course, you will receive a certificate that you can share with employers and add to your professional profile.",
    },
    {
      question: "What is the refund policy?",
      answer: "We offer a 14-day refund policy for our courses. If you are not satisfied with a course, you can request a refund within 14 days of purchase.",
    },
  ];

  const [openIndex, setOpenIndex] = useState(null);

  const toggleAccordion = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <div className="flex flex-wrap items-center justify-center gap-20 p-8 bg-white">
      <div className="w-[35rem] p-3 flex flex-col gap-6 ">
        <div className="text-2xl font-semibold text-[#D1B080] ">
          <h1>FREQUENTLY ASKED QUESTIONS</h1>
        </div>
        <div>
          <h1 className="font-serif text-5xl text-black">You Have a Question?</h1>
        </div>
        <div className="font-thin">
          <p>
            Welcome to EzeCode Academy! We are committed to providing high-quality education and training to help you achieve your career goals. Whether you are just starting out or looking to enhance your skills, we have something for you.
          </p>
        </div>
        <div className="font-thin">
          <p>
            Our expert trainers and industry-aligned courses ensure that you get the best learning experience possible. Explore our FAQ section to find answers to common questions about our platform and courses.
          </p>
        </div>
      </div>
      <div className="flex justify-start">
        <div className="flex flex-col flex-wrap w-full gap-5 p-5">
          {faqData.map((item, index) => (
            <div key={index} className="pb-4 border-b-2 border-orange-300">
              <button
                className="text-lg font-semibold flex justify-between items-center w-full text-left hover:text-[#D1B080] transition duration-300"
                onClick={() => toggleAccordion(index)}
              >
                {item.question}
                <span className={`transform transition-transform duration-300 ${openIndex === index ? 'rotate-180' : ''}`}>
                  â–¼
                </span>
              </button>
              {openIndex === index && (
                <p className="mt-2 text-gray-600">{item.answer}</p>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Faq;
