import React from "react";
import "bootstrap-icons/font/bootstrap-icons.css";
const Pencil = () => {
  const scrollToHeader = () => {
    const header = document.getElementById("header");
    if (header) {
      header.scrollIntoView({ behavior: "smooth" });
    }
  };
  return (
    <div className="">
      <h1>Copyright Â© 2024. All Rights Reserved | WeDesignTech</h1>
      <i
        className="sticky bottom-0 flex items-end justify-end p-4 text-5xl font-bold text-black bg-transparent cursor-pointer bi bi-arrow-up-circle-fill"
        onClick={scrollToHeader}
      ></i>
    </div>
  );
};

export default Pencil;
