import Home from './components/Home'
import './App.css';

function App() {
  const scrollToHeader = () => {
    const header = document.getElementById('header');
    if (header) {
      header.scrollIntoView({ behavior: 'smooth' });
    }
  };
  return (
  <div >
      <Home/>
    <i
    className="sticky bottom-0 flex items-end justify-end text-5xl font-bold text-black bg-transparent cursor-pointer bi bi-pencil" onClick={scrollToHeader}
  ></i>
  </div>

  );
}

export default App;
